                        <input
                          type="time"
                          name="lunch.endTime"
                          id="lunch.endTime"
                          value={formData.lunch.endTime}
                          onChange={handleChange}
                          className={`mt-1 block w-full border rounded-md shadow-sm px-3 py-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm ${
                            fieldErrors['lunch.endTime'] ? 'border-red-500' : 'border-gray-300'
                          }`}
                        />
                        {fieldErrors['lunch.endTime'] && (
                          <p className="mt-1 text-sm text-red-600">{fieldErrors['lunch.endTime']}</p>
