                          className={`mt-1 block w-full border rounded-md shadow-sm px-3 py-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm ${
                            fieldErrors['dinner.endTime'] ? 'border-red-500' : 'border-gray-300'
                          }`}
                        />
                        {fieldErrors['dinner.endTime'] && (
                          <p className="mt-1 text-sm text-red-600">{fieldErrors['dinner.endTime']}</p>
                        )}
                      </div>
                    </div>
